create table big.aaa (id INT NOT NULL AUTO_INCREMENT COMMENT '主鍵' , no CHAR(12) CHARACTER SET ascii COLLATE ascii_general_ci NOT NULL COMMENT '編號' , name VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL COMMENT '名字' , crtdatetime TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '創建資料之系統時間' , PRIMARY KEY (id)) ENGINE = InnoDB;